import User from './users.model';

/**
 * @api {POST} /api/{version}/user Create a new user
 * @apiName CreateUser
 * @apiGroup User
 *
 * @apiPermission public
 *
 * @apiParam {String} req.body.name name of user
 * @apiParam {String} req.body.mobileNumber Mobile phone number
 *
 * @apiSuccess {User} user Returns the created user
 * @apiError {Object} error Error message
 */
function create (req, res, next) {
    const user = new User({
        name: req.body.name,
        email: req.body.email,
        mobileNumber: req.body.mobileNumber
    });

    user.saveAsync()
        .then((savedUser) => res.json(savedUser))
        .error((error) => next(error));
}


/**
 * @api {GET} /api/{version}/user/ List users
 * @apiName ListUser
 * @apiGroup User
 *
 * @apiParam {Number} skip Number of users to be skipped
 * @apiParam {Number} limit Limit of users to be returned
 *
 * @apiParam (Login) {String} pass Only logged in users can post this.
 *
 * @apiSuccess {User[]} users List of user
 * @apiError {Object} error Error message
 */
function list (req, res, next) {
    const {limit = 50, skip = 0} = req.query;

    User.list({limit, skip})
        .then((users) => res.json(users))
        .error((error) => next(error));
}


/**
 * @api {GET} /api/{version}/user/:id Load an user and append to request object
 * @apiName LoadUser
 * @apiGroup User
 *
 * @apiParam {ObjectId} id ObjectId corresponding to and specific user
 *
 * @apiParam (Login) {String} pass Only logged in users can post this.
 *
 * @apiSuccess {User} user Returns an user
 * @apiError {Object} error Error message
 */
function load (req, res, next, id) {
    User.get(id)
        .then((user) => {
            req.user = user;
            return next();
        })
        .error((error) => {
            next(error);
        });
}


/**
 * Get user
 *
 * @apiSuccess {User} Returns an user
 */
function get (req, res) {
    return res.json(req.user);
}


/**
 * @api {DELETE} /api/{version}/user/:id Remove one user from system
 * @apiName RemoveUser
 * @apiGroup User
 *
 * @apiParam {User} user User to be removed
 *
 * @apiParam (Login) {String} pass Only logged in users can post this.
 *
 * @apiSuccess {User} user Returns the deleted user
 * @apiError {Object} error Error message
 */
function remove (req, res, next) {
    const user = req.user;

    user.removeAsync()
        .then((deletedUser) => res.json(deletedUser))
        .error((error) => next(error));
}


/**
 * @api {PUT} /api/{version}/user/:id Update an existing user
 * @apiName UpdateUser
 * @apiGroup User
 *
 * @apiParam {String} name name of user
 * @apiParam {String} mobileNumber Mobile phone number
 *
 * @apiParam (Login) {String} pass Only logged in users can post this.
 *
 * @apiSuccess {User} user Returns the saved user
 * @apiError {Object} error Error message
 */
function update (req, res, next) {
    const user = req.user;

    user.name = req.body.name;
    user.email = req.body.email;
    user.mobileNumber = req.body.mobileNumber;

    user.saveAsync()
        .then((savedUser) => res.json(savedUser))
        .error((error) => next(error));
}


export default {load, get, create, update, list, remove};

import express from 'express';
import validate from 'express-validation';

import userCtrl from './users.controller';
import validator from './users.validator';


const router = express.Router();

/**
 * Main user route
 *
 * {GET/POST} /api/users
 */
router.route('/')
    .get(userCtrl.list)
    .post(validate(validator.createUser), userCtrl.create);

/**
 * Route to an spacific user
 *
 * {GET/PUT/DELETE} /api/users/:userId
 */
router.route('/:userId')
    .get(userCtrl.get)
    .put(validate(validator.updateUser), userCtrl.update)
    .delete(userCtrl.remove);

// Load user when API with userId route parameter is hit
router.param('userId', userCtrl.load);

export default router;

